function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}


function fetchData(url) { 
    return fetch(url).then(response=>response.json()).then(data => {
    return data;} ); 
    }



async function getArtUrl() {

    //Get initial list and pages
    let baseUrl = 'https://api.artic.edu/api/artworks';
    let listing = await fetchData(baseUrl);

    let total_pages = listing["pagination"]["total_pages"]
    let per_page = listing["pagination"]["limit"]

    //Fetch a random item from a random page
    let pageNumber = getRandomInt(0,total_pages);
    let itemNumber = getRandomInt(0,per_page);
    let randPage = await getListing(baseUrl + '?page=' + pageNumber.toString());
    let randItem = randPage['data'][itemNumber];
    let iiifUrl = randPage["config"]["iiif_url"];

    let artUrl = iiifUrl+'/'+randItem["image_id"]+'/full/843,/0/default.jpg';
    return artUrl;


}

export {getArtUrl};
