import './App.css';
import React from 'react';


export default class App extends React.Component {
  render() {
    return (
    <div className="root">

    <ArabicName />
    <Header />
    <Introduction  />
    <ArtCanvas />

    </div>
    );
  }
}

class Header extends React.Component {
  render(){
    return (
      <div className="header">
        <Tab name="about me"/>
        <Tab name="courses"/>
        <Tab name="projects"/>
      </div>
    );
  }
}
class Tab extends React.Component {
  render() {
    return (
      <div class="Tab">
      <h3>{this.props.name}</h3>
      </div>
    );
  }
}
class ArabicName extends React.Component {
  render(){
    return (
      <div className="arabic_name">
          <h1> عماد صدیق </h1>

      </div>
    );
  }
}
class Introduction extends React.Component{
  render(){
    return(
    <div className="introduction">
      <img src="portrait.jpg"/>
      <h1>Hello jee :)</h1>
      </div>
    );
  }
}
let chicago_api_image = "https://www.artic.edu/iiif/2/1adf2696-8489-499b-cad2-821d7fde4b33/full/843,/0/default.jpg";

class ArtCanvas extends React.Component{
  //Render the Chicago API image
  render(){
    return (
      <div className="art">
      <img src={chicago_api_image} alt='Art from Chicago'/>
      <p>This is a random painting from the Art Institute.</p>
      </div>

      );
  }
}
