function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
}


function fetchData(url) { 
    return fetch(url).then(response=>response.json()).then(data => {
    return data;} ); 
    }



async function getArtUrl() {

    //Get initial list and pages
    let baseUrl = 'https://api.artic.edu/api/artworks';
    let totalPages;
    let perPage;

    await fetchData(baseUrl).then(data => {
        totalPages = data["pagination"]["total_pages"];
        perPage = data["pagination"]["limit"];
    });

    //Fetch a random item from a random page
    let pageNumber = getRandomInt(0,totalPages);
    let itemNumber = getRandomInt(0,perPage);

    let artUrl
    await fetchData(baseUrl + '?page=' + pageNumber.toString()).then(randPage => {
        let randItem = randPage['data'][itemNumber];
        let iiifUrl = randPage["config"]["iiif_url"];
        artUrl = iiifUrl+'/'+randItem["image_id"]+'/full/843,/0/default.jpg';


    });
    return artUrl;

    };






export {getArtUrl};
